({
    baseUrl: 'scripts',
    name: 'almond',
    shim: {
        'game': {
            deps: [ 'sounds', 'speech', 'drawShapes', 'globals'],
            exports: 'spacethreat'
        }
    },
    paths:{
        jquery: 'empty:',
        kinetic: 'empty:'
    },
    include: ['game'],
    out: 'build/spacethreat.min.js',
    wrap: {
        start: "(function() {",
        end: "spacethreat = require('game');}());"
    },
})